import type { Meta, StoryObj } from '@storybook/vue3-vite';
import ImagePreview from '../ImagePreview.vue';
import { ref, onMounted } from 'vue';

const meta = {
  title: 'Components/ImagePreview',
  component: ImagePreview,
  tags: ['autodocs'],
  argTypes: {
    width: {
      control: 'number',
      description: 'Width of the image in pixels',
    },
    height: {
      control: 'number',
      description: 'Height of the image in pixels',
    },
    alt: {
      control: 'text',
      description: 'Alternative text for the image',
    },
    imageSrc: {
      control: 'text',
      description: 'Image source URL or base64 string',
    },
  },
} satisfies Meta<typeof ImagePreview>;

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Sample image',
    imageSrc: 'https://picsum.photos/200/200',
  },
};

export const Base64Image: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Base64 image',
    imageSrc: `data:image/webp;base64,`,
  },
  parameters: {
    chromatic: { delay: 500 },
  },
};

export const Loading: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'URL Loading',
    imageSrc: 'https://picsum.photos/200/200?slow',
  },
};

export const SimulatedLoading: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Simulated Load',
    imageSrc: '',
  },
  parameters: {
    chromatic: { disableSnapshot: true },
  },
  render: (args) => ({
    components: { ImagePreview },
    setup() {
      const localArgs = ref({ ...args });
      onMounted(async () => {
        await new Promise((resolve) => setTimeout(resolve, 2000));
        localArgs.value.imageSrc = 'https://picsum.photos/200/200';
      });
      return { localArgs };
    },
    template: '<ImagePreview v-bind="localArgs" />',
  }),
};

export const Error: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Error image',
    imageSrc: 'https://invalid-url.com/image.jpg',
  },
};

export const Small: Story = {
  args: {
    width: 40,
    height: 40,
    alt: 'Small image',
    imageSrc: 'https://picsum.photos/40/40',
  },
};

export const Large: Story = {
  args: {
    width: 400,
    height: 400,
    alt: 'Large image',
    imageSrc: 'https://picsum.photos/400/400',
  },
};

export const InvalidBase64: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Invalid base64',
    imageSrc: 'invalid-base64-string',
  },
};

export const MissingSource: Story = {
  args: {
    width: 200,
    height: 200,
    alt: 'Missing source',
    imageSrc: '',
  },
};

export const PerformanceDisplayWithFiftyImages = {
  render: () => ({
    components: { ImagePreview },
    setup() {
      const images = ref<
        Array<{ id: string; author: string; download_url: string }>
      >([]);
      const isLoading = ref(true);
      const error = ref('');

      onMounted(async () => {
        try {
          const response = await fetch(
            'https://picsum.photos/v2/list?page=2&limit=50',
          );
          if (!response.ok) console.warn('Failed to fetch images');
          images.value = await response.json();
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
        } catch (err: any) {
          error.value = err.message || 'Unknown error';
        } finally {
          isLoading.value = false;
        }
      });

      return { images, isLoading, error };
    },
    template: `
      <div>
        <div v-if="isLoading">Loading images...</div>
        <div v-else-if="error">Error: {{ error }}</div>
        <div v-else style="display: flex; flex-wrap: wrap; gap: 12px;">
          <div v-for="img in images" :key="img.id" style="width: 200px;">
            <ImagePreview :imageSrc="img.download_url" :alt="img.author" :width="200" :height="200" />
          </div>
        </div>
      </div>
    `,
  }),
};
